
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function HouseServicesApp() {
  const navigate = useNavigate();
  const [activeCategory, setActiveCategory] = useState('cleaning');
  const [selectedService, setSelectedService] = useState(null);

  const categories = [
    { id: 'cleaning', name: 'House Cleaning' },
    { id: 'pest', name: 'Pest Control' },
    { id: 'appliances', name: 'Appliance Repair' }
  ];

  // Hero images for each category
  const heroImages = {
    cleaning: "https://plus.unsplash.com/premium_photo-1661679038354-cc7279833968?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTN8fGhvbWUlMjBjbGVhbmluZ3xlbnwwfHwwfHx8MA%3D%3D",
    pest: "https://images.unsplash.com/photo-1650895421894-07954c2134b7?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8N3x8cGVzdCUyMGNvbnRyb2x8ZW58MHx8MHx8fDA%3D",
    appliances: "data:image/jpeg;base64,"
  };

  // Services data
  const services = {
    cleaning: [
      {
        id: 1,
        name: "Full Home Deep Cleaning",
        rating: 4.8,
        imageUrl: "https://www.balajicleaningagency.com/img/service/Untitled-02.jpg",
        reviews: 320,
        price: 1999,
        discountedPrice: 1499,
        time: "4 hours",
        description: "Professional full home deep cleaning service by verified experts",
      },
      {
        id: 2,
        name: "Kitchen Cleaning",
        rating: 4.7,
        imageUrl: "https://spiffynow.com/cdn/shop/files/kitchen_4c4a0a39-5b94-44b9-8afe-6f02fc6896a2.jpg?v=1709224109",
        reviews: 215,
        price: 899,
        discountedPrice: 699,
        time: "1.5 hours",
        description: "Professional kitchen cleaning service by verified experts",
      },
      {
        id: 3,
        name: "Bathroom Sanitization",
        rating: 4.9,
        reviews: 186,
        imageUrl: "https://ecosciencegroups.com/wp-content/uploads/2024/01/bathroom-cleaning.jpg",
        price: 799,
        discountedPrice: 599,
        time: "1 hour",
        description: "Professional bathroom sanitization service by verified experts",
      }
    ],
    pest: [
      {
        id: 1,
        name: "Cockroach & Ant Control",
        rating: 4.7,
        imageUrl: "data:image/jpeg;base64,",
        reviews: 245,
        price: 1299,
        discountedPrice: 999,
        time: "2 hours",
        description: "Professional cockroach and ant control treatment"
      },
      {
        id: 2,
        name: "Mosquito Control",
        rating: 4.6,
        imageUrl: "data:image/jpeg;base64,",
        reviews: 178,
        price: 999,
        discountedPrice: 799,
        time: "1.5 hours",
        description: "Effective mosquito control treatment for your home"
      },
      {
        id: 3,
        name: "Bed Bug Treatment",
        rating: 4.8,
        imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT1MUIoI4An6lk2D_ZAnOaU2cm-XvhKRog7rA&s",
        reviews: 156,
        price: 1599,
        discountedPrice: 1299,
        time: "3 hours",
        description: "Complete bed bug elimination treatment"
      }
    ],
    appliances: [
      {
        id: 1,
        name: "AC Service & Repair",
        rating: 4.8,
        imageUrl: "https://content.jdmagicbox.com/comp/def_content_category/ac-repair-and-services-hyundai/360-f-821590643-skdlcwz7oswxyyblvvs1mhx3stvcx8dz-ac-repair-and-services-hyundai-11-ceyuf.jpg",
        reviews: 310,
        price: 1499,
        discountedPrice: 1199,
        time: "2 hours",
        description: "Professional AC servicing and repair"
      },
      {
        id: 2,
        name: "Refrigerator Repair",
        rating: 4.7,
        imageUrl: "data:image/jpeg;base64,",
        reviews: 187,
        price: 1299,
        discountedPrice: 999,
        time: "1.5 hours",
        description: "Expert refrigerator repair service"
      },
      {
        id: 3,
        name: "Washing Machine Repair",
        rating: 4.6,
        imageUrl: "data:image/jpeg;base64,",
        reviews: 165,
        price: 1199,
        discountedPrice: 899,
        time: "1.5 hours",
        description: "Professional washing machine repair service"
      }
    ]
  };

  // Featured packages data
  const featuredPackages = {
    cleaning: {
      title: "Home Deep Cleaning Package",
      description: "Complete cleaning solution including kitchen, bathroom, living areas and bedrooms",
      rating: 4.9,
      reviews: 420,
      price: 3999,
      discountedPrice: 2999,
      imageUrl: "https://media.istockphoto.com/id/2177545044/photo/woman-spraying-mattress-cloth-with-special-detergents-to-remove-stains.webp?a=1&b=1&s=612x612&w=0&k=20&c=Sq5i7YX3Uj8qrAs5wAPivP8SerZceoHN3F6l6frsxIQ="
    },
    pest: {
      title: "Complete Pest Control Package",
      description: "Comprehensive treatment for cockroaches, ants, mosquitoes and general pests",
      rating: 4.8,
      reviews: 365,
      price: 3499,
      discountedPrice: 2699,
      imageUrl: "https://plus.unsplash.com/premium_photo-1661304818621-0fceeb066f2d?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8NXx8cGVzdCUyMGNvbnRyb2x8ZW58MHx8MHx8fDA%3D"
    },
    appliances: {
      title: "Home Appliance Care Package",
      description: "Maintenance and servicing for AC, refrigerator and washing machine",
      rating: 4.7,
      reviews: 290, 
      price: 3299,
      discountedPrice: 2499,
      imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTElr6eBNqCN-YO7XT-pyFrhs2y4w0t8Exv2Q&s"
    }
  };

  const getActiveServices = () => services[activeCategory];
  const getFeaturedPackage = () => featuredPackages[activeCategory];

  const handleCategoryChange = (category, e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }
    setActiveCategory(category);
    setSelectedService(null);
  };

  const getSectionTitle = () => {
    switch (activeCategory) {
      case 'cleaning':
        return "Professional House Cleaning";
      case 'pest':
        return "Expert Pest Control";
      case 'appliances':
        return "Reliable Appliance Repair";
      default:
        return "Professional House Cleaning";
    }
  };

  const handleBookService = (service, e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }

    try {
      // Calculate discount percentage
      const discountPercentage = Math.round(((service.price - service.discountedPrice) / service.price) * 100);
      
      // Create formatted service data for ProductDetails page
      const serviceData = {
        name: service.name,
        image: {
          src: service.imageUrl || "/api/placeholder/400/320",
          alt: `${service.name} service image`
        },
        basePrices: {
          'Studio': { original: service.price, sale: service.discountedPrice },
          '1 BHK': { original: Math.round(service.price * 1.2), sale: Math.round(service.discountedPrice * 1.2) },
          '2 BHK': { original: Math.round(service.price * 1.4), sale: Math.round(service.discountedPrice * 1.4) },
          '3 BHK': { original: Math.round(service.price * 1.6), sale: Math.round(service.discountedPrice * 1.6) },
          '4 BHK': { original: Math.round(service.price * 1.8), sale: Math.round(service.discountedPrice * 1.8) },
          '5+ BHK': { original: Math.round(service.price * 2), sale: Math.round(service.discountedPrice * 2) }
        },
        discount: `${discountPercentage}% OFF`,
        breadcrumbs: [
          { id: 1, name: 'Home Services', href: '#' },
          { id: 2, name: activeCategory === 'cleaning' ? 'Cleaning' : 
                     activeCategory === 'pest' ? 'Pest Control' : 'Appliance Repair', href: '#' }
        ],
        description: service.description,
        rating: service.rating,
        reviews: service.reviews,
        time: service.time,
        highlights: [
          'Trained and background-checked professionals',
          'Eco-friendly products and equipment',
          'Customizable service options',
          '100% satisfaction guarantee'
        ],
        details: 'Our professionals follow strict safety protocols including wearing masks and sanitizing equipment between appointments. Please ensure access to your property and clear any personal items you don\'t want included in the service.'
      };

      // Navigate to the ProductDetails page with service data
      navigate('/product-details', { state: { service: serviceData } });
    } catch (error) {
      console.error("Navigation error:", error);
    }
  };

  const handleBookPackage = (packageDetails, e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }

    try {
      // Calculate discount percentage
      const discountPercentage = Math.round(((packageDetails.price - packageDetails.discountedPrice) / packageDetails.price) * 100);
      
      // Create formatted package data for ProductDetails page
      const serviceData = {
        name: packageDetails.title,
        image: {
          src: packageDetails.imageUrl || "/api/placeholder/600/200",
          alt: `${packageDetails.title} package image`
        },
        basePrices: {
          'Studio': { original: packageDetails.price * 0.8, sale: packageDetails.discountedPrice * 0.8 },
          '1 BHK': { original: packageDetails.price * 0.9, sale: packageDetails.discountedPrice * 0.9 },
          '2 BHK': { original: packageDetails.price, sale: packageDetails.discountedPrice },
          '3 BHK': { original: packageDetails.price * 1.2, sale: packageDetails.discountedPrice * 1.2 },
          '4 BHK': { original: packageDetails.price * 1.4, sale: packageDetails.discountedPrice * 1.4 },
          '5+ BHK': { original: packageDetails.price * 1.6, sale: packageDetails.discountedPrice * 1.6 }
        },
        discount: `${discountPercentage}% OFF`,
        breadcrumbs: [
          { id: 1, name: 'Home Services', href: '#' },
          { id: 2, name: activeCategory === 'cleaning' ? 'Cleaning Packages' : 
                     activeCategory === 'pest' ? 'Pest Control Packages' : 'Appliance Packages', href: '#' }
        ],
        description: packageDetails.description,
        rating: packageDetails.rating,
        reviews: packageDetails.reviews,
        time: activeCategory === 'cleaning' ? "6 hours" :
              activeCategory === 'pest' ? "4 hours" : "3 hours",
        highlights: [
          'Complete solution for your home',
          'Premium service guarantee',
          'Flexible scheduling options',
          'Priority customer support'
        ],
        details: 'This comprehensive package provides complete coverage for all your home service needs. Our team of experts will coordinate with you to schedule services at your convenience.'
      };

      // Navigate to the ProductDetails page with package data
      navigate('/product-details', { state: { service: serviceData } });
    } catch (error) {
      console.error("Navigation error:", error);
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      <div className="bg-white shadow-sm sticky top-16 z-10">
        <div className="container mx-auto flex overflow-x-auto">
          {categories.map(category => (
            <div
              key={category.id}
              className={`px-6 py-3 font-medium text-sm whitespace-nowrap cursor-pointer ${
                activeCategory === category.id
                  ? 'border-b-2 border-purple-600 text-purple-600'
                  : 'text-gray-600'
              }`}
              onClick={(e) => handleCategoryChange(category.id, e)}
            >
              {category.name}
            </div>
          ))}
        </div>
      </div>

      <main className="flex-grow container mx-auto p-4 mb-16">
        <div className="rounded-lg overflow-hidden mb-6">
          {/* Hero image section - FIXED */}
          {heroImages[activeCategory] ? (
            <img 
              src={heroImages[activeCategory]} 
              alt={`${activeCategory} hero image`}
              className="w-full h-68 object-cover"
            />
          ) : (
            <div className="bg-gray-200 w-full h-88 flex items-center justify-center">
              <p className="text-gray-500">Hero Image for {activeCategory === 'cleaning' ? 'House Cleaning' : activeCategory === 'pest' ? 'Pest Control' : 'Appliance Repair'}</p>
            </div>
          )}
          <div className="bg-white p-4 rounded-b-lg shadow-sm">
            <h2 className="text-xl font-bold">{getSectionTitle()}</h2>
            <div className="flex items-center mt-2">
              <span className="text-yellow-400">★</span>
              <span className="ml-1 text-sm font-semibold">4.8</span>
              <span className="ml-1 text-sm text-gray-500">(1,200+ reviews)</span>
            </div>
            <button
              className="w-full px-10 py-3 mt-4 rounded-lg bg-white border-2 border-black font-medium transition-colors duration-200 hover:bg-black hover:text-white"
              onClick={(e) => handleBookService({
                name: getSectionTitle(),
                rating: 4.8,
                reviews: 1200,
                price: activeCategory === 'cleaning' ? 2999 : activeCategory === 'pest' ? 1999 : 999,
                discountedPrice: activeCategory === 'cleaning' ? 2499 : activeCategory === 'pest' ? 1499 : 799,
                time: activeCategory === 'cleaning' ? "5 hours" : activeCategory === 'pest' ? "3 hours" : "2 hours",
                imageUrl: "/api/placeholder/400/320",
                description: `Professional ${getSectionTitle()} by verified experts`
              }, e)}
            >
              Book Service
            </button>
          </div>
        </div>

        <div className="mb-4">
          <h3 className="text-lg font-bold">Popular Services</h3>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {getActiveServices().map(service => (
            <div
              key={service.id}
              className="bg-white rounded-lg shadow-sm overflow-hidden cursor-pointer"
              onClick={() => setSelectedService(service)}
            >
              <div className="relative">
                {/* Service image - FIXED */}
                {service.imageUrl ? (
                  <img 
                    src={service.imageUrl} 
                    alt={service.name}
                    className="w-full h-40 object-cover"
                  />
                ) : (
                  <div className="bg-gray-200 w-full h-40 flex items-center justify-center">
                    <p className="text-gray-500">Image for {service.name}</p>
                  </div>
                )}
                <span className="absolute top-2 right-2 bg-yellow-100 text-yellow-800 text-xs font-medium px-2 py-1 rounded-full">
                  Bestseller
                </span>
              </div>
              <div className="p-4">
                <h4 className="font-semibold text-base">{service.name}</h4>
                <div className="flex items-center mt-1 mb-2">
                  <span className="text-yellow-400">★</span>
                  <span className="ml-1 text-xs font-medium">{service.rating}</span>
                  <span className="ml-1 text-xs text-gray-500">({service.reviews})</span>
                </div>
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center text-xs text-gray-500">
                    <span className="mr-1">⏱️</span>
                    <span>{service.time}</span>
                  </div>
                  <div>
                    <span className="text-xs line-through text-gray-400">₹{service.price}</span>
                    <span className="ml-1 font-semibold">₹{service.discountedPrice}</span>
                  </div>
                </div>
                <button
                  className="w-full px-4 py-2 rounded-lg bg-white border-2 border-black font-medium transition-colors duration-200 hover:bg-black hover:text-white"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleBookService(service, e);
                  }}
                >
                  Book Service
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-8">
          <h3 className="text-lg font-bold mb-4">Recommended Package</h3>
          <div className="bg-white rounded-lg shadow-sm overflow-hidden">
            {/* Featured package image - FIXED */}
            {getFeaturedPackage().imageUrl ? (
              <img 
                src={getFeaturedPackage().imageUrl} 
                alt={getFeaturedPackage().title}
                className="w-full h-80 object-cover"
              />
            ) : (
              <div className="bg-gray-200 w-full h-40 flex items-center justify-center">
                <p className="text-gray-500">Image for {getFeaturedPackage().title}</p>
              </div>
            )}
            <div className="p-4">
              <div className="flex flex-col md:flex-row justify-between items-start">
                <div>
                  <h4 className="font-semibold text-base">{getFeaturedPackage().title}</h4>
                  <div className="flex items-center mt-1">
                    <span className="text-yellow-400">★</span>
                    <span className="ml-1 text-xs font-medium">{getFeaturedPackage().rating}</span>
                    <span className="ml-1 text-xs text-gray-500">({getFeaturedPackage().reviews})</span>
                  </div>
                  <div className="mt-2 text-sm text-gray-600">
                    {getFeaturedPackage().description}
                  </div>
                </div>
                <div className="mt-4 md:mt-0 md:text-right w-full md:w-auto">
                  <div className="text-xs line-through text-gray-400">₹{getFeaturedPackage().price}</div>
                  <div className="font-semibold">₹{getFeaturedPackage().discountedPrice}</div>
                  <button
                    className="w-60 md:w-auto px-10 py-3 mt-3 rounded-lg bg-white border-2 border-black font-medium transition-colors duration-200 hover:bg-black hover:text-white"
                    onClick={(e) => handleBookPackage(getFeaturedPackage(), e)}
                  >
                    Book Package
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
  

export default HouseServicesApp;